package week1.day2;

public class LearnControlStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// To Print number 3 in words
		int i;

		for (i = 0; i < 5; i++) {
			if (i == 3)
				System.out.println("Three");
			else
				System.out.println(i);
		}
	

	}

}
